mapper.select:
select id, name, url, imgUrl, createDate, updateDate, createUserId, updateUserId, shopId, platformId, type, status from b2c_mall_config_image
id = #{id} and platformId = #{platformId}
and shopId = #{shopId} and shopId is null

mapper.update:
update b2c_mall_config_image set updateUserId = #{updateUser.id}, status = #{status}, updateDate = now()

    id = #{id} and platformId = #{platformId}
    
        and shopId = #{shopId}
        and shopId is null
